import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles.css';

const Kanban = () => {
    const [tasks, setTasks] = useState({
        a_fazer: [],
        fazendo: [],
        pronto: []
    });

    const statusOptions = [
        { value: 'a_fazer', label: 'A Fazer' },
        { value: 'fazendo', label: 'Fazendo' },
        { value: 'pronto', label: 'Pronto' }
    ];

    // Função para buscar as tarefas
    const fetchTasks = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:8000/api/tasks/');
            // Organize as tarefas por status
            const organizedTasks = {
                a_fazer: response.data.filter(task => task.status === 'a_fazer'),
                fazendo: response.data.filter(task => task.status === 'fazendo'),
                pronto: response.data.filter(task => task.status === 'pronto'),
            };
            setTasks(organizedTasks);
        } catch (error) {
            console.error("Erro ao buscar as tarefas:", error);
        }
    };

    // Atualiza o status da tarefa
    const handleStatusChange = async (taskId, newStatus) => {
        try {
            // Atualiza o status no backend
            await axios.put(`http://127.0.0.1:8000/api/tasks/${taskId}/`, { status: newStatus });

            // Atualiza o estado local imediatamente
            setTasks(prevTasks => {
                const updatedTasks = { ...prevTasks }; // Copia o estado atual
                // Remove a tarefa da coluna antiga
                Object.keys(updatedTasks).forEach(status => {
                    updatedTasks[status] = updatedTasks[status].filter(task => task.id !== taskId);
                });
                // Encontra a tarefa atualizada
                const updatedTask = Object.values(prevTasks)
                    .flat()
                    .find(task => task.id === taskId);

                // Atualiza o status da tarefa
                if (updatedTask) {
                    updatedTask.status = newStatus; // Altera o status localmente
                    updatedTasks[newStatus].push(updatedTask); // Adiciona na nova coluna
                }
                return updatedTasks;
            });
        } catch (error) {
            console.error("Erro ao atualizar o status da tarefa:", error);
        }
    };

    // Chama a função quando o componente é montado
    useEffect(() => {
        fetchTasks();
    }, []);

    return (
        <div className="kanban">
            {Object.keys(tasks).map(status => (
                <div key={status} className="kanban-column">
                    <h2>{statusOptions.find(option => option.value === status).label}</h2>
                    {tasks[status].map(task => (
                        <div key={task.id} className="kanban-card">
                            <h3>{task.descricao}</h3>
                            <p><strong>Setor:</strong> {task.setor}</p>
                            <p><strong>Prioridade:</strong> {task.prioridade}</p>
                            <p><strong>Usuário:</strong> {task.username}</p>
                            <p><strong>Data de Cadastro:</strong> {new Date(task.data_cadastro).toLocaleDateString()}</p>
                            <div>
                                <button
                                    onClick={() => handleStatusChange(task.id, 'a_fazer')}
                                    disabled={status === 'a_fazer'}
                                >
                                    A Fazer
                                </button>
                                <button
                                    onClick={() => handleStatusChange(task.id, 'fazendo')}
                                    disabled={status === 'fazendo'}
                                >
                                    Fazendo
                                </button>
                                <button
                                    onClick={() => handleStatusChange(task.id, 'pronto')}
                                    disabled={status === 'pronto'}
                                >
                                    Pronto
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default Kanban;
